<?phpclass Knockoutphase extends Controller {	public function index() {        if ($user_id = logged_in()) {            $vars['predictions'] = Doctrine_Query::create()                ->select('p.match_number,                          m.match_name,                          m.match_time,                          m.home_goals,                          m.away_goals,                          m.home_id,                          m.match_number,                          th.name,                          th.flag,                          ta.name,                          ta.flag,                          v.name,                          v.time_offset_utc')                ->from('Predictions p INDEXBY p.match_number, p.Match m, m.TeamHome th, m.TeamAway ta, m.Venue v, p.User u')                ->where('m.type_id < 6')                ->andWhere('u.id ='.$user_id)                ->orderBy('m.match_time')                ->setHydrationMode(Doctrine::HYDRATE_ARRAY)                ->execute();            $vars['title'] = "Knockout Phase Overview";            $vars['content_view'] = "knockoutphase";		            $vars['settings'] = $this->settings_functions->settings();            $this->load->view('template', $vars);            }	}}