<?phpclass Knockoutphase extends Controller {	public function index() {                $vars['matches'] = Doctrine_Query::create()            ->select('m.match_name,                      m.match_time,                      m.home_goals,                      m.away_goals,                      m.home_id,                      m.match_number,                      th.name,                      th.flag,                      ta.name,                      ta.flag,                      v.name')            ->from('Matches m, m.TeamHome th, m.TeamAway ta, m.Venue v')            ->where('m.type_id < 6')            ->orderBy('m.match_time')            ->execute();		        $vars['title'] = "Knockout Phase Overview";		$vars['content_view'] = "knockoutphase";				$vars['settings'] = $this->settings_functions->settings();		$this->load->view('template', $vars);	}}